BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "django_migrations" (
	"id"	integer NOT NULL,
	"app"	varchar(255) NOT NULL,
	"name"	varchar(255) NOT NULL,
	"applied"	datetime NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_group_permissions" (
	"id"	integer NOT NULL,
	"group_id"	integer NOT NULL,
	"permission_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("permission_id") REFERENCES "auth_permission"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("group_id") REFERENCES "auth_group"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "auth_user_groups" (
	"id"	integer NOT NULL,
	"user_id"	integer NOT NULL,
	"group_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("user_id") REFERENCES "auth_user"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("group_id") REFERENCES "auth_group"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "auth_user_user_permissions" (
	"id"	integer NOT NULL,
	"user_id"	integer NOT NULL,
	"permission_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("user_id") REFERENCES "auth_user"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("permission_id") REFERENCES "auth_permission"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "django_admin_log" (
	"id"	integer NOT NULL,
	"action_time"	datetime NOT NULL,
	"object_id"	text,
	"object_repr"	varchar(200) NOT NULL,
	"change_message"	text NOT NULL,
	"content_type_id"	integer,
	"user_id"	integer NOT NULL,
	"action_flag"	smallint unsigned NOT NULL CHECK("action_flag" >= 0),
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("content_type_id") REFERENCES "django_content_type"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("user_id") REFERENCES "auth_user"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "django_content_type" (
	"id"	integer NOT NULL,
	"app_label"	varchar(100) NOT NULL,
	"model"	varchar(100) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_permission" (
	"id"	integer NOT NULL,
	"content_type_id"	integer NOT NULL,
	"codename"	varchar(100) NOT NULL,
	"name"	varchar(255) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("content_type_id") REFERENCES "django_content_type"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "auth_group" (
	"id"	integer NOT NULL,
	"name"	varchar(150) NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_user" (
	"id"	integer NOT NULL,
	"password"	varchar(128) NOT NULL,
	"last_login"	datetime,
	"is_superuser"	bool NOT NULL,
	"username"	varchar(150) NOT NULL UNIQUE,
	"last_name"	varchar(150) NOT NULL,
	"email"	varchar(254) NOT NULL,
	"is_staff"	bool NOT NULL,
	"is_active"	bool NOT NULL,
	"date_joined"	datetime NOT NULL,
	"first_name"	varchar(150) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "django_session" (
	"session_key"	varchar(40) NOT NULL,
	"session_data"	text NOT NULL,
	"expire_date"	datetime NOT NULL,
	PRIMARY KEY("session_key")
);
CREATE TABLE IF NOT EXISTS "news_slicenews" (
	"id"	char(32) NOT NULL,
	"created_at"	datetime NOT NULL,
	"title"	varchar(255),
	"body_html"	text,
	"url"	varchar(200) NOT NULL,
	"image_url"	varchar(200) NOT NULL,
	"image_name"	varchar(5000),
	"image_caption"	varchar(5000),
	"attribution"	varchar(5000),
	"source"	varchar(255),
	"publish_date"	datetime,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "twitter_longesttrend" (
	"link"	varchar(200),
	"pos"	integer,
	"trend_name"	varchar(255),
	"tweets"	varchar(255),
	"id"	char(32) NOT NULL,
	"created"	datetime NOT NULL,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "twitter_tophashtagtrend" (
	"link"	varchar(200),
	"pos"	integer,
	"trend_name"	varchar(255),
	"tweets"	varchar(255),
	"id"	char(32) NOT NULL,
	"created"	datetime NOT NULL,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "twitter_vietnamtwittertrend" (
	"link"	varchar(200),
	"trend_name"	varchar(255),
	"id"	char(32) NOT NULL,
	"created"	datetime NOT NULL,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "twitter_worldwidetwittertrend" (
	"count"	varchar(255),
	"link"	varchar(200),
	"pos"	integer,
	"trend_name"	varchar(255),
	"id"	char(32) NOT NULL,
	"created"	datetime NOT NULL,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "youtube_vietnammusicvideo" (
	"pos"	integer NOT NULL,
	"vid_name"	varchar(255),
	"vid_link"	varchar(255),
	"id"	char(32) NOT NULL,
	"created"	datetime NOT NULL,
	"embed_link"	varchar(255),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "youtube_vietnamtrendvideo" (
	"pos"	integer NOT NULL,
	"vid_name"	varchar(255),
	"vid_link"	varchar(255),
	"id"	char(32) NOT NULL,
	"created"	datetime NOT NULL,
	"embed_link"	varchar(255),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "news_articlenews" (
	"id"	char(32) NOT NULL,
	"created_at"	datetime NOT NULL,
	"title"	varchar(255),
	"abstract"	varchar(10000),
	"url"	varchar(200) NOT NULL,
	"publish_date"	datetime,
	"image_url"	varchar(200) NOT NULL,
	"attribution"	varchar(255),
	"image_name"	varchar(5000),
	"image_caption"	varchar(5000),
	"source"	varchar(255),
	"reaction_count"	integer NOT NULL,
	"content"	text,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "others_keyword" (
	"id"	char(32) NOT NULL,
	"created_at"	datetime NOT NULL,
	"keyword"	varchar(255),
	"source"	varchar(255),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "ggtrends_trendposts" (
	"id"	char(32) NOT NULL,
	"title"	varchar(255),
	"link"	varchar(255),
	"body"	varchar(50000),
	"trend_name_id"	char(32),
	"created"	datetime,
	PRIMARY KEY("id"),
	FOREIGN KEY("trend_name_id") REFERENCES "ggtrends_todaytrends"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "ggtrends_todaytrends" (
	"id"	char(32) NOT NULL,
	"trend_name"	varchar(255),
	"created"	datetime,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "ggtrends_yeartrend" (
	"year_trend"	integer,
	"trends"	text CHECK((JSON_VALID("trends") OR "trends" IS NULL)),
	"created"	datetime,
	"id"	char(32) NOT NULL,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "news_comment" (
	"id"	integer NOT NULL,
	"name"	varchar(80) NOT NULL,
	"email"	varchar(254) NOT NULL,
	"body"	text NOT NULL,
	"created_on"	datetime NOT NULL,
	"active"	bool NOT NULL,
	"post_id"	char(32) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("post_id") REFERENCES "news_articlenews"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "facebook_post" (
	"id"	char(32) NOT NULL,
	"name"	varchar(255),
	"shares"	integer NOT NULL,
	"reaction_count"	integer NOT NULL,
	"comments"	integer,
	"content"	varchar(255),
	"posted_on"	varchar(255),
	"video"	varchar(255),
	"image"	varchar(255),
	"post_url"	varchar(200),
	"likes"	integer NOT NULL,
	"loves"	integer NOT NULL,
	"wow"	integer NOT NULL,
	"sad"	integer NOT NULL,
	"angry"	integer NOT NULL,
	"haha"	integer NOT NULL,
	"cares"	integer NOT NULL,
	"cursor"	varchar(1000),
	"page_id"	integer NOT NULL,
	"post_id"	integer NOT NULL,
	"updated_on"	varchar(255),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "others_contact" (
	"id"	char(32) NOT NULL,
	"email"	varchar(254) NOT NULL,
	"subject"	varchar(255) NOT NULL,
	"message"	text NOT NULL,
	"created_at"	datetime NOT NULL,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "others_rakemodel" (
	"id"	integer NOT NULL,
	"paragraph"	text,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "django_celery_beat_intervalschedule" (
	"id"	integer NOT NULL,
	"every"	integer NOT NULL,
	"period"	varchar(24) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "django_celery_beat_periodictasks" (
	"ident"	smallint NOT NULL,
	"last_update"	datetime NOT NULL,
	PRIMARY KEY("ident")
);
CREATE TABLE IF NOT EXISTS "django_celery_beat_periodictask" (
	"id"	integer NOT NULL,
	"name"	varchar(200) NOT NULL UNIQUE,
	"task"	varchar(200) NOT NULL,
	"args"	text NOT NULL,
	"kwargs"	text NOT NULL,
	"queue"	varchar(200),
	"exchange"	varchar(200),
	"routing_key"	varchar(200),
	"expires"	datetime,
	"enabled"	bool NOT NULL,
	"last_run_at"	datetime,
	"total_run_count"	integer unsigned NOT NULL CHECK("total_run_count" >= 0),
	"date_changed"	datetime NOT NULL,
	"description"	text NOT NULL,
	"crontab_id"	integer,
	"interval_id"	integer,
	"solar_id"	integer,
	"one_off"	bool NOT NULL,
	"start_time"	datetime,
	"priority"	integer unsigned CHECK("priority" >= 0),
	"headers"	text NOT NULL,
	"clocked_id"	integer,
	"expire_seconds"	integer unsigned CHECK("expire_seconds" >= 0),
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("solar_id") REFERENCES "django_celery_beat_solarschedule"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("crontab_id") REFERENCES "django_celery_beat_crontabschedule"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("interval_id") REFERENCES "django_celery_beat_intervalschedule"("id") DEFERRABLE INITIALLY DEFERRED,
	FOREIGN KEY("clocked_id") REFERENCES "django_celery_beat_clockedschedule"("id") DEFERRABLE INITIALLY DEFERRED
);
CREATE TABLE IF NOT EXISTS "django_celery_beat_clockedschedule" (
	"id"	integer NOT NULL,
	"clocked_time"	datetime NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "django_celery_beat_solarschedule" (
	"id"	integer NOT NULL,
	"latitude"	decimal NOT NULL,
	"longitude"	decimal NOT NULL,
	"event"	varchar(24) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "django_celery_beat_crontabschedule" (
	"id"	integer NOT NULL,
	"minute"	varchar(240) NOT NULL,
	"hour"	varchar(96) NOT NULL,
	"day_of_week"	varchar(64) NOT NULL,
	"day_of_month"	varchar(124) NOT NULL,
	"month_of_year"	varchar(64) NOT NULL,
	"timezone"	varchar(63) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "ggtrends_trendhotposts" (
	"id"	char(32) NOT NULL,
	"title"	varchar(255),
	"link"	varchar(255),
	"body"	varchar(50000),
	"created"	datetime,
	"trend_name"	varchar(100),
	PRIMARY KEY("id")
);
