INSERT INTO "django_admin_log" ("id","action_time","object_id","object_repr","change_message","content_type_id","user_id","action_flag") VALUES (1,'2022-04-25 16:35:23.980532','b536b064-a625-4e17-951c-0ad45ae208b8','Như Quỳnh đến từ Gia Lai. Cô đang theo học ngành Ngôn ngữ Anh tại Đại học Phương Đông.','',7,1,3),
 (2,'2022-04-25 16:35:24.108403','ae794593-fbbb-4ab1-99d3-6a01fafb993c','Sau nhiều năm nổi tiếng, hiện tại Phương Thảo theo đuổi gu thời trang gợi cảm, táo bạo hơn.','',7,1,3),
 (3,'2022-04-25 16:35:24.209841','a3cbc065-3241-4ab5-a59a-671c78a06c37','Mai Ngọc là nữ MC VTV được nhiều người ngưỡng mộ không chỉ vì sở hữu xinh đẹp, cô còn có cuộc sống sang chảnh vạn người mê.','',7,1,3),
 (4,'2022-04-25 16:35:24.308874','9608cd31-bf0a-4ed2-b095-78c348b5842a','Như Quỳnh đến từ Gia Lai. Cô đang theo học ngành Ngôn ngữ Anh tại Đại học Phương Đông.','',7,1,3),
 (5,'2022-04-25 16:35:24.410132','90d45bfc-d9a3-44ec-9062-65294a53aaff','Sau nhiều năm nổi tiếng, hiện tại Phương Thảo theo đuổi gu thời trang gợi cảm, táo bạo hơn.','',7,1,3),
 (6,'2022-04-25 16:35:24.507826','88b16823-c0ff-49c5-a47c-a8404cbf049d','Ban đầu, Duy chỉ mua thử vài chậu về trồng. Theo thời gian, tích lũy được nhiều kinh nghiệm, Hoàng Duy mạnh dạn mua thêm nhiều giống cây khác nhau.','',7,1,3),
 (7,'2022-04-25 16:35:24.597336','87bbbb34-7b37-4789-9323-61e5c4b39074','Vũ Như Quỳnh vừa đoạt giải Người đẹp được yêu thích nhất vòng chung khảo Miss World Vietnam 2022.','',7,1,3),
 (8,'2022-04-25 16:35:24.674523','84460cf6-3c41-49f3-85e0-132bd66e8912','Vũ Như Quỳnh vừa đoạt giải Người đẹp được yêu thích nhất vòng chung khảo Miss World Vietnam 2022.','',7,1,3),
